            throw new IllegalArgumentException("Invalid XPath: Must start with //");
        }

        // Remove leading //
        xpath = xpath.substring(2);

        // Initialize UISelector string
        StringBuilder uiSelector = new StringBuilder("UiSelector()");

        // Handle tag name
        String[] parts = xpath.split("\\[", 2);
        String tagName = parts[0];
        if (!tagName.equals("*")) {
            uiSelector.append(".className(\"").append(tagName).append("\")");
        }

        // Handle attributes
        if (parts.length > 1) {
            String attributePart = parts[1].substring(0, parts[1].length() - 1);
            String[] attributes = attributePart.split(" and ");

            for (String attribute : attributes) {
                attribute = attribute.trim();
                uiSelector.append(convertAttributeToUiSelector(attribute));
            }
        }

        return uiSelector.toString();
    }

    private static String convertAttributeToUiSelector(String attribute) {
        // Remove @
        attribute = attribute.replace("@", "");

        // Handle different attribute types
