import datetime

def get_last_quarter_start(today):
  """Returns the start date of the last quarter given a date."""
  year = today.year
  month = today.month
  if month in [1, 2, 3]:
    return datetime.date(year - 1, 10, 1)
  elif month in [4, 5, 6]:
    return datetime.date(year, 1, 1)
  elif month in [7, 8, 9]:
    return datetime.date(year, 4, 1)
  else:
    return datetime.date(year, 7, 1)

# Example usage:
today = datetime.date(2023, 5, 15)
last_quarter_start = get_last_quarter_start(today)
print(last_quarter_start)  # Output: 2023-01-01

today = datetime.date(2022, 3, 31)
last_quarter_start = get_last_quarter_start(today)
print(last_quarter_start)  # Output: 2021-10-01
```

