import io.appium.java_client.MobileBy;

public class XPathToUISelectorConverter {

    public static void main(String[] args) {
        String xpath = "//android.widget.TextView[@text='Hello World']";
        String uiselector = convertXPathToUISelector(xpath);
        System.out.println("UISelector: " + uiselector);
    }

    public static String convertXPathToUISelector(String xpath) {
        String uiselector = "";
        if (xpath.startsWith("//")) {
            String[] parts = xpath.split("/");
            for (String part : parts) {
                if (part.startsWith("@text='")) {
                    String text = part.substring(part.indexOf("'") + 1, part.lastIndexOf("'"));
                    uiselector = "new UiSelector().text(\"" + text + "\")";
                }
            }
        }
        return uiselector;
    }
}
```

