    WordCounter(const std::string& file, const std::string& word, bool sensitive = true)
        : filename(file), searchWord(word), caseSensitive(sensitive), wordCount(0) {}

    // Method to count word occurrences
    int countWordOccurrences() {
        std::ifstream file(filename);

        // Check if file is opened successfully
        if (!file.is_open()) {
            std::cerr << "Error: Unable to open file " << filename << std::endl;
            return -1;
        }

        std::string line;
        wordCount = 0;

        // Read file line by line
        while (std::getline(file, line)) {
            // Process line based on case sensitivity
            if (!caseSensitive) {
                line = toLowercase(line);
                std::string lowercaseSearchWord = toLowercase(searchWord);
                wordCount += countWordInLine(line, lowercaseSearchWord);
            } else {
                wordCount += countWordInLine(line, searchWord);
            }
        }

        file.close();
        return wordCount;
    }

