#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::ifstream file("input.txt"); // Open the text file
    std::string wordToSearch = "hello"; // Word to search for
    std::string line;
    int wordCount = 0;

    if(file.is_open()){
        while(getline(file, line)){ // Read the file line by line
            size_t pos = 0;
            while((pos = line.find(wordToSearch, pos)) != std::string::npos){ // Find occurrences of the word
                wordCount++;
                pos += wordToSearch.length();
            }
        }
        file.close(); // Close the file
        std::cout << "Number of occurrences of \"" << wordToSearch << "\" in the file: " << wordCount << std::endl; // Output the count
    }
    else{
        std::cout << "Unable to open file." << std::endl;
    }

    return 0;
}
```

